import React, {useState} from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';

const SideNav = (props) => {
  const [loading, setLoading] = useState(false);

  const handleButtonClick = async (i) => {
    setLoading(true);
    await new Promise((resolve) => setTimeout(resolve, 1000));

    // Perform your actual onClick logic here
    props._setCurrentEducation(i);

    setLoading(false);
  };
  const values = [];
  for(let i=0;i<props.data.length;++i){
    values.push(
        <li className="nav-item">
            <button
                type="button"
                className="btn btn-outline-dark btn-lg m-3 shadow-none border-0"
                style={{
                    color: '#333', // Text color
                    transition: 'color 1s ease', // Smooth color transition on hover
                }}
                onMouseEnter={(e) => (e.target.style.color = '#fff')} 
                onMouseLeave={(e) => (e.target.style.color = '#333')}
                onClick={(e) => (handleButtonClick(i))}
                disabled={loading}
                >
                {loading?(<span class="spinner-border spinner-border-sm" aria-hidden="true"></span>):<></>}
                <span role="status">{props.data[i].institution} {loading}</span>
            </button>
        </li>
    )
  }
  return (
    <div className="d-flex flex-column align-items-start p-3">
      <nav>
        <ul className="nav flex-column">
          {values}
        </ul>
      </nav>
    </div>
  );
};

export default SideNav;
