import React from "react";
import './styles/skills.css'
import SideNav from "./Sidenav";
export default function Skills(){
  const [index, setCurrentEducation] = useState(0);

    const educationData = [
        {
            institution: "Katholieke Universiteit, Leuven",
            degree: "International Scholar",
            courses: ["Master's thesis at IMEC, Belgium"],
            thesis: {
                text: "Master's Thesis",
                link: "#"
            }
        },
        {
            institution: 'Indian Statistical Institute, Kolkata',
            degree: 'M.Tech at Computer Science',
            courses: ['Introduction to Programming', 'Data Structures', 'Algorithm Design'],
            thesis: {
                text: "Master's Thesis",
                link: "#"
            }
        },
        {
            institution: 'Jadavpur University, Kolkata',
            degree: 'B.E in Electrical Engineering',
            courses: ['Electrical Engineering'],
            thesis:{
                text: "Bachelor's Thesis",
                link: "#"
            }
        }
    ];
  const skills = [
    {
      text: "C",
      desp: "First Programming Language"
    },
    {
      text: "C++",
      desp: "Yeah I love this one"
    },
    {
      text: "Python",
      desp: "Cant help it. Have to lean it"
    },
    {
      text: "Javascript",
      desp: "Google and good to go"
    },
    {
      text: "HTML",
      desp: "Dont know what to say"
    },
    {
      text: "CSS",
      desp: ":< Dont know what to say"
    },
    {
      text: "ReactJS",
      desp: "Nice One from Facebook"
    },
    {
      text: "Django Rest",
      desp: "Yes I know Backend Stuffs too"
    },
    {
      text: "MongoDB",
      desp: "Database connectivity xD"
    }
  ]
    return (
      <div className="container mt-2 d-flex">
        <SideNav data={educationData} _setCurrentEducation={setCurrentEducation}/>
        <div className="container d-flex flex-wrap justify-content-center">
          {skills.map((skill,index)=>(
            <div class="card m-3">
              <div class="card-inner">
                <div class="card-front text-center">
                  <h3>{skill.text}</h3>
                </div>
                <div class="card-back text-center">
                  <p>{skill.desp}</p>
                </div>
              </div>
          </div>
          ))}</div>
    </div>
    )
};
