import React from "react";
import { Link } from "react-router-dom";
export default function EducationItem(props){
    const _data = props.data;
    return (
        <div className="conatiner">
            <div key={_data.institution} className='card-body mt-1'>
            <h2 className="card-text sexy-heading">{_data.institution}</h2>
            <p className=''>{_data.degree}</p>
            {_data.courses.map((__data,i) => (
                <p>{__data}</p>
            ))}
            <p className="justify-content">{_data.details}</p>
            <button type="button" class="btn btn-link"><Link to={_data.thesis.link}>{_data.thesis.text}</Link></button>
        </div>
        </div>
    )
}
