import React, { useState, useEffect } from 'react';
// import TypingEffect from 'react-typing-effect';
import './styles/home.css'
import profilePic from './assets/profile-pic.jpeg'

export default function Home(){
    const [showAnimation, setShowAnimation] = useState(false);

    useEffect(() => {
      const animationTimeout = setTimeout(() => {
        setShowAnimation(true);
      }, 500);

      return () => clearTimeout(animationTimeout);
    }, []);


    return (
      <div className="justify-content-left">
        <div className="profile-pic-container">
          <img src={profilePic} alt="Profile Pic" className="profile-pic"/>
          <div className={`profile-paragraph ${showAnimation ? 'fade-in' : ''}`}>
            <p className="typing-text">
              Proficient in data structures and algorithms, I have a strong inclination towards software development, formal verification, and distributed systems. My interest extends to theoretical aspects of computer science, where I explore the elegance of complex systems. With a focus on crafting efficient solutions, I am also keen on applying formal verification for software reliability in distributed environments.
            </p>
          </div>
        </div>
      </div>
    )
}
