              <div>
                {skill.institution === skilltype[index].institution?(
                <div class="card m-3">
                  <div class="card-inner">
                    <div class="card-front text-center">
                      <h3>{skill.text}</h3>
                    </div>
                    <div class="card-back text-center">
                      <p>{skill.desp}</p>
                    </div>
                  </div>
              </div>):<></>}
          </div>))}
        </div>
      </div>
