import React, {useState} from "react";
import './styles/skills.css'
import SideNav from "./Sidenav";
export default function Skills(){
  const [index, setCurrentEducation] = useState(0);
  const skilltype = [
    {institution:"Programming Language"},
    {institution: "Framework"},
    {institution: "Databases"},
    {institution: "IDE Used"},
    {institution: "Operating Systems"}
  ]
  const skills = [
    {
      text: "C",
      desp: "First Programming Language",
      institution: "Programming Language"
    },
    {
      text: "C++",
      desp: "Yeah I love this one",
      institution: "Programming Language"
    },
    {
      text: "Python",
      desp: "Cant help it. Have to lean it",
      institution: "Programming Language"
    },
    {
      text: "Javascript",
      desp: "Google and good to go",
      institution: "Programming Language"
    },
    {
      text: "HTML",
      desp: "Dont know what to say",
      institution: "Programming Language"
    },
    {
      text: "CSS",
      desp: ":< Dont know what to say",
      institution: "Programming Language"
    },
    {
      text: "ReactJS",
      desp: "Nice One from Facebook",
      institution: "Framework"
    },
    {
      text: "Django Rest",
      desp: "Yes I know Backend Stuffs too",
      institution: "Framework"
    },
    {
      text: "MongoDB",
      desp: "Database connectivity xD",
      institution: "Databases"
    },
    {
      text: "MySQL",
      desp: "Duh Obviously",
      institution: "Databases"
    },
    {
      text: "SQLite",
      desp: "I know a bit of python, you know :)",
      institution: "Databases"
    },
    {
      text: "VS Code",
      desp: "Everyone knows",
      institution: "IDE Used"
    },
    {
      text: "PyCharm",
      desp: "Django Stuffs xD",
      institution: "IDE Used"
    },
    {
      text: "Sublime Text",
      desp: "Dude why not",
      institution: "IDE Used"
    },
    {
      text: "Linux-Ubuntu",
      desp: "I mean why not :)",
      institution: "Operating Systems"
    },
    {
      text: "MacOS",
      desp: "I have some money",
      institution: "Operating Systems"
    },
    {
      text: "Windows",
      desp: "Only because I had to",
      institution: "Operating Systems"
    }
  ]
    return (
    <div className="container mt-1 d-flex">
      <SideNav data={skilltype} _setCurrentEducation={setCurrentEducation}/>
      <div className="custom-dev">
        <h3>{skilltype[index].institution}</h3>
        <div className="container mt-1 d-flex">
          <div className="container d-flex flex-wrap justify-content-center">
            {skills.map((skill, _i)=>(
              <div>
                {skill.institution === skilltype[index].institution?(
                <div class="card m-3">
                  <div class="card-inner">
                    <div class="card-front text-center">
                      <h3>{skill.text}</h3>
                    </div>
                    <div class="card-back text-center">
                      <p>{skill.desp}</p>
                    </div>
                  </div>
              </div>):<></>}
          </div>))}
        </div>
      </div>
    </div>
    </div>
    )
};
