import React from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';

const SideNav = (props) => {
  const values = [];
  for(let i=0;i<props.data.length;++i){
    values.push(
        <li className="nav-item">
            <button
      type="button"
      className="btn btn-outline-dark btn-lg rounded-pill shadow-none"
      style={{
        // Additional custom styling
        color: '#333', // Text color
        transition: 'color 0.3s ease', // Smooth color transition on hover
      }}
      onMouseEnter={(e) => (e.target.style.color = '#fff')} // Change text color on hover
      onMouseLeave={(e) => (e.target.style.color = '#333')} // Restore text color on mouse leave
    >
      {props.data}
    </button>
            <a className="nav-link active" href="#">
                {props.data[i].institution}
            </a>
        </li>
    )
  }
  return (
    <div className="d-flex flex-column align-items-start p-3">
      <nav>
        <ul className="nav flex-column">
          {values}
        </ul>
      </nav>
    </div>
  );
};

export default SideNav;
