import React from 'react';
import { useState } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';
import SideNav from './Sidenav';
import ExperienceItem from './Experience-item';
const Experience = () => {
    const [index, setCurrentEducation] = useState(0);

    const educationData = [
        {
            institution: "Predigle India",
            designation: "Software Engineer-L3",
            description: ["Wrote Testing Framework for testing Django Rest APIs","Wrote Django APIs that will help to run background process and stop when necessary",
            "Designed and Developed Atlassian JIRA B2B plugin for process intake step of a project."
        ]
        },
        {
            institution: "IMEC, Belgium",
            designation: "International Scholar",
            description: ["Master's thesis"],
        },
        {
            institution: 'Airtel India',
            designation: 'Engineering Intern',
            description: ['Introduction to Programming', 'Data Structures', 'Algorithm Design'],
        },
        {
            institution: 'IIT Kgp',
            degree: 'Research Intern',
            courses: ['Advanced Software Development', 'Distributed Systems', 'Formal Methods'],
        }
    ];
  return (
    <div className="container mt-2 d-flex">
        <SideNav data={educationData} _setCurrentEducation={setCurrentEducation}/>
        <ExperienceItem data={educationData[index]} />
    </div>
  );
};

export default Experience;
