import React from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';

const SideNav = (props) => {
  const handleButtonClick = async (i) => {
    // props.data[i].loader = true;
    new Promise((resolve) => setTimeout(resolve, 500));
    props.data[i].loader = true
    props._setCurrentEducation(i);
    props.data[i].loader = false;
  };
  const values = [];
  for(let i=0;i<props.data.length;++i){
    values.push(
        <li className="nav-item">
            <button
                type="button"
                className="btn btn-outline-dark btn-lg m-3 shadow-none border-0"
                style={{
                    color: '#333', // Text color
                    transition: 'color 1s ease', // Smooth color transition on hover
                }}
                onMouseEnter={(e) => (e.target.style.color = '#fff')} 
                onMouseLeave={(e) => (e.target.style.color = '#333')}
                onClick={() => (handleButtonClick(i))}
                >
                <span role="status">{props.data[i].institution}</span>
                {props.data[i].loader?(<span class="spinner-border spinner-border-sm" aria-hidden="true"></span>):<></>}
            </button>
        </li>
    )
  }
  return (
    <div className="d-flex flex-column align-items-start p-3">
      <nav>
        <ul className="nav flex-column">
          {values}
        </ul>
      </nav>
    </div>
  );
};

export default SideNav;
