import React, { useState, useEffect } from 'react';
import './styles/sectionloading.css'; // Import your custom CSS file

const GoogleLoadingScreen = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setLoading(false);
    }, 500);

    // Clear the timeout if the component is unmounted
    return () => clearTimeout(timeout);
  }, []);

  return (
    <div className={`google-loading-screen ${loading ? 'loading' : 'loaded'}`}>
      <div className="google-spinner"></div>
      {!loading && (
        // Render your actual content here after the timeout
        <></>
      )}
    </div>
  );
};

export default GoogleLoadingScreen;
