import React from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';
import SideNav from './Sidenav';
const educationData = [
    {
        institution: "Katholieke Universiteit Leuven",
        degree: "International Scholar",
        courses: ["master's thesis at IMEC, Belgium"],
        clicked: true
    },
    {
        institution: 'Indian Statistical Institute, Kolkata',
        degree: 'M.Tech at Computer Science',
        courses: ['Introduction to Programming', 'Data Structures', 'Algorithm Design'],
        clicked: false
    },
    {
        institution: 'Jadavpur University, Kolkata',
        degree: 'B.E in Electrical Engineering',
        courses: ['Advanced Software Development', 'Distributed Systems', 'Formal Methods'],
        clicked: false
    }
];
const Education = () => {
  return (
    <div className="container mt-5 d-flex">
      <SideNav data={educationData}/>
      <div>
      {educationData.map((education, index) => (
        <div>
        {education.clicked?(
        <div key={index} className="card mt-3">
          <div className="card-body">
            <h5 className="card-title">{education.institution}</h5>
            <p className="card-text">{education.degree}</p>
            <ul>
              {education.courses.map((course, i) => (
                <li key={i}>{course}</li>
              ))}
            </ul>
          </div>
        </div>):``}</div>
      ))}
      </div>
    </div>
  );
};

export default Education;
