import React from 'react';
import { useState } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';
import SideNav from './Sidenav';
import LoadingScreen from './LoadingScreen';
const Education = () => {
    const [index, setCurrentEducation] = useState(0);

    const educationData = [
        {
            institution: "Katholieke Universiteit Leuven",
            degree: "International Scholar",
            courses: ["Master's thesis at IMEC, Belgium"],
            thesis: {
                text: "Master's Thesis",
                link: "#"
            }
        },
        {
            institution: 'Indian Statistical Institute, Kolkata',
            degree: 'M.Tech at Computer Science',
            courses: ['Introduction to Programming', 'Data Structures', 'Algorithm Design'],
            thesis: ""
        },
        {
            institution: 'Jadavpur University, Kolkata',
            degree: 'B.E in Electrical Engineering',
            courses: ['Advanced Software Development', 'Distributed Systems', 'Formal Methods'],
            thesis:""
        }
    ];
  return (
    <div className="container mt-2 d-flex">
        <SideNav data={educationData} _setCurrentEducation={setCurrentEducation}/>
        <div className="conatiner">
            <div key={educationData[index]} className='card-body mt-1'>
            <h5 className='card-text'>{educationData[index].institution}</h5>
            <p className=''>{educationData[index].degree}</p>
            {educationData[index].courses.map((data,i) => (
                <p>{data}</p>
            ))}
        </div>
        <button type="button" class="btn btn-link"></button>
        </div>
    </div>
  );
};

export default Education;
